import './musicplay.css';
import React, { useEffect, useState } from "react";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import SkipNextIcon from '@mui/icons-material/SkipNext';
import IconButton from '@mui/material/IconButton';
import ShuffleIcon from '@mui/icons-material/Shuffle';
import RepeatIcon from '@mui/icons-material/Repeat';
const tracks = [
  {
    name: "Mekanın Sahibi",
    artist: "Norm Ender",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/1.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/1.mp3",
    url: "https://www.youtube.com/watch?v=z3wAjJXbYzA",
    favorited: true
  },
  {
    name: "Everybody Knows",
    artist: "Leonard Cohen",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/2.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/2.mp3",
    url: "https://www.youtube.com/watch?v=Lin-a2lTelg",
    favorited: true
  },
  {
    name: "Extreme Ways",
    artist: "Moby",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/3.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/3.mp3",
    url: "https://www.youtube.com/watch?v=ICjyAe9S54c",
    favorited: false
  },
  {
    name: "Butterflies",
    artist: "Sia",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/4.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/4.mp3",
    url: "https://www.youtube.com/watch?v=kYgGwWYOd9Y",
    favorited: false
  },
  {
    name: "The Final Victory",
    artist: "Haggard",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/5.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/5.mp3",
    url: "https://www.youtube.com/watch?v=0WlpALnQdN8",
    favorited: true
  },
  {
    name: "Genius ft. Sia, Diplo, Labrinth",
    artist: "LSD",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/6.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/6.mp3",
    url: "https://www.youtube.com/watch?v=HhoATZ1Imtw",
    favorited: false
  },
  {
    name: "The Comeback Kid",
    artist: "Lindi Ortega",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/7.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/7.mp3",
    url: "https://www.youtube.com/watch?v=me6aoX0wCV8",
    favorited: true
  },
  {
    name: "Overdose",
    artist: "Grandson",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/8.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/8.mp3",
    url: "https://www.youtube.com/watch?v=00-Rl3Jlx-o",
    favorited: false
  },
  {
    name: "Rag'n'Bone Man",
    artist: "Human",
    cover: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/img/9.jpg",
    source: "https://raw.githubusercontent.com/muhammederdem/mini-player/master/mp3/9.mp3",
    url: "https://www.youtube.com/watch?v=L3wKzyIN1yk",
    favorited: false
  }];
const player = new Audio(tracks[0].source);
player.setAttribute('preload','metadata');
const userOptions = React.createContext({
    shuffle:false,
    repeat:false
});

export default function MusicPlay(props) {
  let options =React.useContext(userOptions);
  let [msuffle,setMsuffle]=useState(options.shuffle);
  let [mrepeat,setMrepeat] =useState(options.repeat);
  let [mfavorite,setFavorite] = useState(tracks[0].favorited);
  useEffect(
      ()=>{ setFavorite(tracks[0].favorited)}) 
      function shuffle (){
          options.shuffle=!options.shuffle
          options.repeat=false;
          setMsuffle(!msuffle);
          setMrepeat(false);

      }
      function repeat (){
         options.repeat=!options.repeat
         options.shuffle=false;
         setMrepeat(!mrepeat)
         setMsuffle(false)


      }
      function favorite(){
          tracks[0].favorited=!tracks[0].favorited;
          setFavorite(tracks[0].favorited);

      }

  const theme = useTheme();
  return (
    <div className="card_box">
      <Card sx={{ minHeight: '60%', minWidth: '50%', margin: 'auto', borderRadius: '1rem' }}>

        {
          mfavorite &&
          <Box sx={{ marginLeft: '90%', marginTop: '2%' }} onClick={favorite}>
          <FavoriteIcon sx={{color:'#E1306C'}}/>
        </Box>
        ||
        <Box sx={{ marginLeft: '90%', marginTop: '2%',cursor:'pointer' }} onClick={favorite}>
          <FavoriteIcon />
        </Box>
        }
        <CardMedia
          sx={{ height: '250px', width: '250px', borderRadius: '50%', margin: 'auto' }}
          image="https://i.ytimg.com/vi/BcOODsQBhgY/maxresdefault.jpg"
          title="green iguana"
        />
        <CardContent>
          
          <Typography gutterBottom variant="subtitle1" fontWeight='bold' textAlign='center' component="div">
            Sakambari(Fulmaya)
          </Typography>
         
        </CardContent>
      </Card>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', pl: 1, pb: 1 }}>
        {
          msuffle &&
          <IconButton aria-label='suffle' onClick={shuffle}>
            <ShuffleIcon />
          </IconButton>
          ||
          <IconButton aria-label='suffle' onClick={shuffle}>
            <ShuffleIcon sx={{color:'#E1306C',cursor:'pointer'}}/>
          </IconButton>
        }
        <IconButton aria-label="previous">
          {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
        </IconButton>
        <IconButton aria-label="play/pause">
          <PlayArrowIcon sx={{ height: 38, width: 38 }} />
        </IconButton>
        <IconButton aria-label="next">
          {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
        </IconButton>
        
       { mrepeat && 
       <IconButton aria-aria-label='repeat'>
          <RepeatIcon onClick={repeat} sx={{color:'#E1306C',cursor:'pointer'}} />
         
        </IconButton>
        ||
        <IconButton aria-aria-label='repeat'>
          <RepeatIcon onClick={repeat} />
         
        </IconButton>}
      </Box>

    </div>
  );
}
